import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '0400 123 456' }
  ]) 
  const [ newName, setNewName ] = useState('')
  const [ newNumber, setNewNumber] = useState('')
  const [ showAll, setShowAll ] = useState(true)
  const [ search, setSearch] = useState('')

  const addName = (event) => {
      event.preventDefault()
      const nameObject = {
          name: newName,
          number: newNumber
      }
      if(persons.some(l => l.name === newName)) {
        window.alert(`${newName} is already added to the phonebook`)
      }
      else {
        setPersons(persons.concat(nameObject))
        setNewName('')
        setNewNumber('')
      }
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }
  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }
  const handleSearchChange = (event) => {
    setSearch(event.target.value)
    if (search === '') setShowAll(true)
    else if (showAll) setShowAll(false)

    console.log('>', event.target.value, '€', showAll)
  } 

  const personsToShow = showAll
    ? persons
    : persons.map(l => l.toString().slice(0, search.length)).filter(l => l === search)

  return (
    <div>
      <h2>Phonebook</h2>
      filter shown with
      <input 
        onChange = {handleSearchChange}
      />
      <h2>Add a new</h2>
      <form onSubmit={addName}>
        <div> name: 
          <input 
            value = {newName}
            onChange = {handleNameChange}
          />
        </div>
        <div> number:
          <input
            value = {newNumber}
            onChange = {handleNumberChange}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
        {personsToShow.map(person => 
            <li key={person.name}>
                {person.name}: {person.number}
            </li> 
        )}
    </div>
  )

}

export default App
